#include <bits/stdc++.h>  //
using namespace std;
int tot=0,n;
int vis[30][3],C[20];  //C
inline void outPut(){
    tot++;
    if(tot<=3){
        for(int i=1;i<=n;i++) cout<<C[i]<<" ";
        cout<<endl;
    }
}
void search(int cur){
    if(cur>n) outPut();
    else for(int i=1;i<=n;i++){
